/**
    This component is used to display a modal for adding a branch, so that the user can select 
    the branches they want to allow reservations for.
*/
<template>
  <div class="modal fade" :id="modalId" tabindex="-1" :aria-labelledby="`${modalId}Label`" aria-hidden="true">
    <div class="modal-dialog modal-size">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" :id="`${modalId}Label`">{{ $t('addBranch') }}</h5>
          <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body">
          <div class="mb-3">
            <p class="text-center"> {{ $t('addBranchDescription') }} </p>
            <ReservationSelectionTable 
              :branches="nonReservationsBranches" 
              :submitSelection="handleSubmit"
            />
          </div>
        </div>
        <div class="modal-footer">
          <FoodicsPrimaryBtn data-bs-dismiss="modal" @click="$emit('close')">{{ $t('close') }}</FoodicsPrimaryBtn>
        <FoodicsPrimaryBtn 
            @click="handleSubmit"
            :disabled="!selectedBranch"
          >
            Save
          </FoodicsPrimaryBtn>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import ReservationSelectionTable from '../tables/ReservationSelectionTable.vue'
import FoodicsPrimaryBtn from '../buttons/FoodicsPrimaryBtn.vue'
import FoodicsSecondaryBtn from '../buttons/FoodicsSecondaryBtn.vue'

export default {
  name: 'AddBranchModal',
  components: {
    ReservationSelectionTable,
    FoodicsPrimaryBtn,
    FoodicsSecondaryBtn
  },
  props: {
    modalId: {
      type: String,
      required: true
    },
    nonReservationsBranches: {
      type: Array,
      required: true,
      default: () => []
    },
    pagesCount: {
      type: Number,
      required: true,
      default: 0
    },
    /**
     * @param {Array<BranchOverviewModel>} branches - Array of branches to allow reservations
     */
    submitSelection: {
      type: Function,
      required: true
    }
  },
  data() {
    return {
      selectedBranch: '',
      currPage: 0 // starts from 0
    }
  },
  methods: {
    handleSubmit() {
      this.submitSelection(this.selectedBranch)
      this.selectedBranch = ''
    }
  }
}
</script>

<style scoped>
.modal-size {
  width: 80vw;
  min-width: 80vw;
}

@media (min-width: 768px) {
  .modal-size {
    width: 55vw;
    min-width: 55vw;
  }
}
.modal-header {
  border-bottom: 1px solid #dee2e6;
}

.modal-footer {
  border-top: 1px solid #dee2e6;
}
</style>
