/**
    This component is used to display a table of branches with a checkbox for each branch.
    It allows the user to select or deselect all branches.
*/
<template>
  <table class="table">
    <thead>
      <tr>
        <th scope="col">
          <div class="form-check">
            <input class="form-check-input" type="checkbox" v-model="selectAll" @change="toggleSelectAll">
            <label class="form-check-label">Select All</label>
          </div>
        </th>
        <th scope="col">{{ $t('name') }}</th>
        <th scope="col">{{ $t('reference') }}</th>
      </tr>
    </thead>
    <tbody>
      <tr v-for="branch in branches" :key="branch.reference" class="border-bottom-0 table-row">
        <td>
          <div class="form-check">
            <input class="form-check-input" type="checkbox" v-model="selectedBranches" :value="branch">
          </div>
        </td>
        <td>{{ branch.name }}</td>
        <td>{{ branch.reference }}</td>
      </tr>
    </tbody>
  </table>
</template>

<script>
export default {
  name: 'ReservationSelectionTable',
  props: {
    branches: {
      type: Array,
      required: true,
      default: () => []
    },
    submitSelection: {
      type: Function,
      required: true
    }
  },
  data() {
    return {
      selectedBranches: [],
      selectAll: false
    }
  },
  methods: {
    toggleSelectAll() {
      if (this.selectAll) {
        this.selectedBranches = [...this.branches]
      } else {
        this.selectedBranches = []
      }
    }
  },
  watch: {
    selectedBranches(newVal) {
      this.selectAll = newVal.length === this.branches.length
      this.$emit('selection-changed', newVal)
      if (this.selectAll) {
        this.selectedBranch = []
      }
    }
  }
}
</script>

<style scoped>
.table {
  width: 100%;
  margin-bottom: 1rem;
  color: #212529;
  vertical-align: top;
  border-color: #dee2e6;
}

.table > :not(caption) > * > * {
  padding: 0.5rem;
  border-bottom-width: 1px;
}

.table-row:hover {
  background-color: #f8f9fa !important;
}

</style>
